/**
               * 给数组分组
               * @param $arr 将要处理的数组
               * @param $key 要分组的key名
               * @return array
               */
               function group_same_key($arr, $key)
               {
               $new_arr = array();
               foreach ($arr as $k = $v) {
               $new_arr[$v[$key]][] = $v;
               }
               return $new_arr;
               }

